import React, { useEffect } from 'react';

const GoogleTranslate = () => {
  useEffect(() => {
    // Function to initialize Google Translate
    const googleTranslateElementInit = () => {
      new window.google.translate.TranslateElement({ pageLanguage: 'en', layout: window.google.translate.TranslateElement.InlineLayout.SIMPLE }, 'google_translate_element');
    };

    // Adding Google Translate script dynamically
    const script = document.createElement('script');
    script.type = 'text/javascript';
    script.src = '//translate.google.com/translate_a/element.js?cb=googleTranslateElementInit';
    document.body.appendChild(script);

    return () => {
      // Cleanup function to remove script when component unmounts
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div id="google_translate_element">
      {/* Placeholder for Google Translate button */}
    </div>
  );
};

const App = () => {
  return (
    <div>
      <header>
        <h1>KrishiMitra</h1>
        <p>You Sow, We Help You Grow</p>
        {/* Render Google Translate component */}
        <GoogleTranslate />
      </header>

      {/* Other sections and content here */}

      <footer>
        {/* Footer content */}
      </footer>
    </div>
  );
};

export default <Translate></Translate>;
